write:
if cmd[6:] in os.listdir():
    with open(cmd[6:], 'w') as file:
        final = ''
        line = ''
        while line != '!:exit':
            line = input('')
            if line != '!:exit':
                final += line + '\n'
        file.write(final)
else:
    print(f'Error: File \'{cmd[6:]}\' does not exist.')

